","keccak256":"0x882028bbad965788430b37558ea12b191a31b4d6e562c08ac2357fa6e0bc9de5","license":"MIT"},"contracts/libraries/LibAsset.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { TransferFailed } from "../utils/GenericErrors.sol";

/**
 * @title LibAsset
 * @dev This library provides functions for interacting with ERC20 assets.
 */
library LibAsset {
    uint256 private constant MAX_UINT = type(uint256).max;

    /**
     * @notice Approves a specified amount of an asset for a spender if the current allowance is insufficient.
     * @param asset The address of the asset.
     * @param spender The address of the spender.
     * @param amount The amount to approve.
     */
    function maxApprove(address asset, address spender, uint256 amount) internal {
        if (IERC20(asset).allowance(address(this), spender) < amount) {
            SafeERC20.safeApprove(IERC20(asset), spender, 0);
            SafeERC20.safeApprove(IERC20(asset), spender, amount);
        }
    }

    /**
     * @notice Transfers a specified amount of an asset from one address to another.
     * @param asset The address of the asset.
     * @param from The sender's address.
     * @param to The recipient's address.
     * @param amount The amount to transfer.
     */
    function transferFrom(address asset, address from, address to, uint256 amount) internal {
        uint256 prevBalance = IERC20(asset).balanceOf(to);
        SafeERC20.safeTransferFrom(IERC20(asset), from, to, amount);
        if (IERC20(asset).balanceOf(to) - prevBalance != amount) {
            revert TransferFailed();
        }
    }

    /**
     * @notice Transfers a specified amount of an asset to a recipient.
     * @param asset The address of the asset.
     * @param to The recipient's address.
     * @param amount The amount to transfer.
     */
    function transfer(address asset, address to, uint256 amount) internal {
        uint256 prevBalance = IERC20(asset).balanceOf(to);
        SafeERC20.safeTransfer(IERC20(asset), to, amount);
        if (IERC20(asset).balanceOf(to) - prevBalance != amount) {
            revert TransferFailed();
        }
    }

    /**
     * @notice Retrieves the balance of a specified asset for a given account.
     * @param asset The address of the asset.
     * @param account The account for which to check the balance.
     * @return The balance of the asset for the specified account.
     */
    function balanceOf(address asset, address account) internal view returns (uint256) {
        return IERC20(asset).balanceOf(account);
    }
}
","keccak256":"0xabaf52a5d90146866c805817efee408b56be4cf4c1dd529fd3634d2bf4abed0e","license":"MIT"},"contracts/libraries/LibBytes.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

library LibBytes {
    // solhint-disable no-inline-assembly

    // LibBytes specific errors
    error SliceOverflow();
    error SliceOutOfBounds();
    error AddressOutOfBounds();

    bytes16 private constant _SYMBOLS = "0123456789abcdef";

    // -------------------------
    /**
     * @notice Slice a portion of a bytes array.
     * @param _bytes The input bytes array.
     * @param _start The starting index of the slice.
     * @param _length The length of the slice.
     * @return tempBytes A new bytes array containing the sliced data.
     */
    function slice(bytes memory _bytes, uint256 _start, uint256 _length) internal pure returns (bytes memory) {
        if (_length + 31 < _length) revert SliceOverflow();
        if (_bytes.length < _start + _length) revert SliceOutOfBounds();

        bytes memory tempBytes;

        assembly {
            switch iszero(_length)
            case 0 {
                // Get a location of some free memory and store it in tempBytes as
                // Solidity does for memory variables.
                tempBytes := mload(0x40)

                // The first word of the slice result is potentially a partial
                // word read from the original array. To read it, we calculate
                // the length of that partial word and start copying that many
                // bytes into the array. The first word we copy will start with
                // data we don't care about, but the last `lengthmod` bytes will
                // land at the beginning of the contents of the new array. When
                // we're done copying, we overwrite the full first word with
                // the actual length of the slice.
                let lengthmod := and(_length, 31)

                // The multiplication in the next line is necessary
                // because when slicing multiples of 32 bytes (lengthmod == 0)
                // the following copy loop was copying the origin's length
                // and then ending prematurely not copying everything it should.
                let mc := add(add(tempBytes, lengthmod), mul(0x20, iszero(lengthmod)))
                let end := add(mc, _length)

                for {
                    // The multiplication in the next line has the same exact purpose
                    // as the one above.
                    let cc := add(add(add(_bytes, lengthmod), mul(0x20, iszero(lengthmod))), _start)
                } lt(mc, end) {
                    mc := add(mc, 0x20)
                    cc := add(cc, 0x20)
                } {
                    mstore(mc, mload(cc))
                }

                mstore(tempBytes, _length)

                //update free-memory pointer
                //allocating the array padded to 32 bytes like the compiler does now
                mstore(0x40, and(add(mc, 31), not(31)))
            }
            //if we want a zero-length slice let's just return a zero-length array
            default {
                tempBytes := mload(0x40)
                //zero out the 32 bytes slice we are about to return
                //we need to do it because Solidity does not garbage collect
                mstore(tempBytes, 0)

                mstore(0x40, add(tempBytes, 0x20))
            }
        }

        return tempBytes;
    }

    /**
     * @notice Convert a portion of a bytes array to an address.
     * @param _bytes The input bytes array.
     * @param _start The starting index of the address data.
     * @return tempAddress The address extracted from the bytes array.
     */
    function toAddress(bytes memory _bytes, uint256 _start) internal pure returns (address) {
        if (_bytes.length < _start + 20) {
            revert AddressOutOfBounds();
        }
        address tempAddress;

        assembly {
            tempAddress := div(mload(add(add(_bytes, 0x20), _start)), 0x1000000000000000000000000)
        }

        return tempAddress;
    }

    /// Copied from OpenZeppelin's `Strings.sol` utility library.
    /// https://github.com/OpenZeppelin/openzeppelin-contracts/blob/8335676b0e99944eef6a742e16dcd9ff6e68e609/contracts/utils/Strings.sol
    /**
     * @notice Convert a uint256 to its hexadecimal representation as a string.
     * @param value The uint256 value to convert.
     * @param length The desired length of the hexadecimal string.
     * @return A string containing the hexadecimal representation of the uint256 value.
     */
    function toHexString(uint256 value, uint256 length) internal pure returns (string memory) {
        bytes memory buffer = new bytes(2 * length + 2);
        buffer[0] = "0";
        buffer[1] = "x";
        for (uint256 i = 2 * length + 1; i > 1; --i) {
            buffer[i] = _SYMBOLS[value & 0xf];
            value >>= 4;
        }
        require(value == 0, "Strings: hex length insufficient");
        return string(buffer);
    }
}
","keccak256":"0xbd7edc3743a6a3ca135ec7077a7e2f29c45719980c324eed18c084e15293a503","license":"MIT"},"contracts/libraries/LibDiamond.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { IDiamondCut } from "../interfaces/IDiamondCut.sol";
import { AppStorage } from "../AppStorage.sol";

// Remember to add the loupe functions from DiamondLoupeFacet to the diamond.
// The loupe functions are required by the EIP2535 Diamonds standard

error InitializationFunctionReverted(address _initializationContractAddress, bytes _calldata);

library LibDiamond {
    event DiamondCut(IDiamondCut.FacetCut[] _diamondCut, address _init, bytes _calldata);
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    bytes32 constant CLEAR_ADDRESS_MASK = bytes32(uint256(0xffffffffffffffffffffffff));
    bytes32 constant CLEAR_SELECTOR_MASK = bytes32(uint256(0xffffffff << 224));

    function diamondStorage() internal pure returns (AppStorage storage s) {
        assembly {
            s.slot := 0
        }
    }

    function setContractOwner(address _newOwner) internal {
        AppStorage storage s = diamondStorage();
        address previousOwner = s.owner;
        s.owner = _newOwner;
        emit OwnershipTransferred(previousOwner, _newOwner);
    }

    // Internal function version of diamondCut
    // This code is almost the same as the external diamondCut,
    // except it is using 'Facet[] memory _diamondCut' instead of
    // 'Facet[] calldata _diamondCut'.
    // The code is duplicated to prevent copying calldata to memory which
    // causes an error for a two dimensional array.
    function diamondCut(IDiamondCut.FacetCut[] memory _diamondCut, address _init, bytes memory _calldata) internal {
        AppStorage storage s = diamondStorage();
        uint256 originalSelectorCount = s.selectorCount;
        uint256 selectorCount = originalSelectorCount;
        bytes32 selectorSlot;
        // Check if last selector slot is not full
        // "selectorCount & 7" is a gas efficient modulo by eight "selectorCount % 8"
        if (selectorCount & 7 > 0) {
            // get last selectorSlot
            // "selectorSlot >> 3" is a gas efficient division by 8 "selectorSlot / 8"
            selectorSlot = s.selectorSlots[selectorCount >> 3];
        }
        // loop through diamond cut
        for (uint256 facetIndex; facetIndex < _diamondCut.length; ) {
            (selectorCount, selectorSlot) = addReplaceRemoveFacetSelectors(
                selectorCount,
                selectorSlot,
                _diamondCut[facetIndex].facetAddress,
                _diamondCut[facetIndex].action,
                _diamondCut[facetIndex].functionSelectors
            );

            unchecked {
                facetIndex++;
            }
        }
        if (selectorCount != originalSelectorCount) {
            s.selectorCount = uint16(selectorCount);
        }
        // If last selector slot is not full
        // "selectorCount & 7" is a gas efficient modulo by eight "selectorCount % 8"
        if (selectorCount & 7 > 0) {
            // "selectorSlot >> 3" is a gas efficient division by 8 "selectorSlot / 8"
            s.selectorSlots[selectorCount >> 3] = selectorSlot;
        }
        emit DiamondCut(_diamondCut, _init, _calldata);
        initializeDiamondCut(_init, _calldata);
    }

    function addReplaceRemoveFacetSelectors(
        uint256 _selectorCount,
        bytes32 _selectorSlot,
        address _newFacetAddress,
        IDiamondCut.FacetCutAction _action,
        bytes4[] memory _selectors
    ) internal returns (uint256, bytes32) {
        AppStorage storage s = diamondStorage();
        require(_selectors.length > 0, "LibDiamondCut: No selectors in facet to cut");
        if (_action == IDiamondCut.FacetCutAction.Add) {
            enforceHasContractCode(_newFacetAddress, "LibDiamondCut: Add facet has no code");
            for (uint256 selectorIndex; selectorIndex < _selectors.length; ) {
                bytes4 selector = _selectors[selectorIndex];
                bytes32 oldFacet = s.facets[selector];
                require(
                    address(bytes20(oldFacet)) == address(0),
                    "LibDiamondCut: Can't add function that already exists"
                );
                // add facet for selector
                s.facets[selector] = bytes20(_newFacetAddress) | bytes32(_selectorCount);
                // "_selectorCount & 7" is a gas efficient modulo by eight "_selectorCount % 8"
                // " << 5 is the same as multiplying by 32 ( * 32)
                uint256 selectorInSlotPosition = (_selectorCount & 7) << 5;
                // clear selector position in slot and add selector
                _selectorSlot =
                    (_selectorSlot & ~(CLEAR_SELECTOR_MASK >> selectorInSlotPosition)) |
                    (bytes32(selector) >> selectorInSlotPosition);
                // if slot is full then write it to storage
                if (selectorInSlotPosition == 224) {
                    // "_selectorSlot >> 3" is a gas efficient division by 8 "_selectorSlot / 8"
                    s.selectorSlots[_selectorCount >> 3] = _selectorSlot;
                    _selectorSlot = 0;
                }
                _selectorCount++;

                unchecked {
                    selectorIndex++;
                }
            }
        } else if (_action == IDiamondCut.FacetCutAction.Replace) {
            enforceHasContractCode(_newFacetAddress, "LibDiamondCut: Replace facet has no code");
            for (uint256 selectorIndex; selectorIndex < _selectors.length; ) {
                bytes4 selector = _selectors[selectorIndex];
                bytes32 oldFacet = s.facets[selector];
                address oldFacetAddress = address(bytes20(oldFacet));
                // only useful if immutable functions exist
                require(oldFacetAddress != address(this), "LibDiamondCut: Can't replace immutable function");
                require(
                    oldFacetAddress != _newFacetAddress,
                    "LibDiamondCut: Can't replace function with same function"
                );
                require(oldFacetAddress != address(0), "LibDiamondCut: Can't replace function that doesn't exist");
                // replace old facet address
                s.facets[selector] = (oldFacet & CLEAR_ADDRESS_MASK) | bytes20(_newFacetAddress);

                unchecked {
                    selectorIndex++;
                }
            }
        } else if (_action == IDiamondCut.FacetCutAction.Remove) {
            require(_newFacetAddress == address(0), "LibDiamondCut: Remove facet address must be address(0)");
            // "_selectorCount >> 3" is a gas efficient division by 8 "_selectorCount / 8"
            uint256 selectorSlotCount = _selectorCount >> 3;
            // "_selectorCount & 7" is a gas efficient modulo by eight "_selectorCount % 8"
            uint256 selectorInSlotIndex = _selectorCount & 7;
            for (uint256 selectorIndex; selectorIndex < _selectors.length; ) {
                if (_selectorSlot == 0) {
                    // get last selectorSlot
                    selectorSlotCount--;
                    _selectorSlot = s.selectorSlots[selectorSlotCount];
                    selectorInSlotIndex = 7;
                } else {
                    selectorInSlotIndex--;
                }
                bytes4 lastSelector;
                uint256 oldSelectorsSlotCount;
                uint256 oldSelectorInSlotPosition;
                // adding a block here prevents stack too deep error
                {
                    bytes4 selector = _selectors[selectorIndex];
                    bytes32 oldFacet = s.facets[selector];
                    require(
                        address(bytes20(oldFacet)) != address(0),
                        "LibDiamondCut: Can't remove function that doesn't exist"
                    );
                    // only useful if immutable functions exist
                    require(
                        address(bytes20(oldFacet)) != address(this),
                        "LibDiamondCut: Can't remove immutable function"
                    );
                    // replace selector with last selector in s.facets
                    // gets the last selector
                    // " << 5 is the same as multiplying by 32 ( * 32)
                    lastSelector = bytes4(_selectorSlot << (selectorInSlotIndex << 5));
                    if (lastSelector != selector) {
                        // update last selector slot position info
                        s.facets[lastSelector] = (oldFacet & CLEAR_ADDRESS_MASK) | bytes20(s.facets[lastSelector]);
                    }
                    delete s.facets[selector];
                    uint256 oldSelectorCount = uint16(uint256(oldFacet));
                    // "oldSelectorCount >> 3" is a gas efficient division by 8 "oldSelectorCount / 8"
                    oldSelectorsSlotCount = oldSelectorCount >> 3;
                    // "oldSelectorCount & 7" is a gas efficient modulo by eight "oldSelectorCount % 8"
                    // " << 5 is the same as multiplying by 32 ( * 32)
                    oldSelectorInSlotPosition = (oldSelectorCount & 7) << 5;
                }
                if (oldSelectorsSlotCount != selectorSlotCount) {
                    bytes32 oldSelectorSlot = s.selectorSlots[oldSelectorsSlotCount];
                    // clears the selector we are deleting and puts the last selector in its place.
                    oldSelectorSlot =
                        (oldSelectorSlot & ~(CLEAR_SELECTOR_MASK >> oldSelectorInSlotPosition)) |
                        (bytes32(lastSelector) >> oldSelectorInSlotPosition);
                    // update storage with the modified slot
                    s.selectorSlots[oldSelectorsSlotCount] = oldSelectorSlot;
                } else {
                    // clears the selector we are deleting and puts the last selector in its place.
                    _selectorSlot =
                        (_selectorSlot & ~(CLEAR_SELECTOR_MASK >> oldSelectorInSlotPosition)) |
                        (bytes32(lastSelector) >> oldSelectorInSlotPosition);
                }
                if (selectorInSlotIndex == 0) {
                    delete s.selectorSlots[selectorSlotCount];
                    _selectorSlot = 0;
                }

                unchecked {
                    selectorIndex++;
                }
            }
            _selectorCount = selectorSlotCount * 8 + selectorInSlotIndex;
        } else {
            revert("LibDiamondCut: Incorrect FacetCutAction");
        }
        return (_selectorCount, _selectorSlot);
    }

    function initializeDiamondCut(address _init, bytes memory _calldata) internal {
        if (_init == address(0)) {
            return;
        }
        enforceHasContractCode(_init, "LibDiamondCut: _init address has no code");
        (bool success, bytes memory error) = _init.delegatecall(_calldata);
        if (!success) {
            if (error.length > 0) {
                // bubble up error
                /// @solidity memory-safe-assembly
                assembly {
                    let returndata_size := mload(error)
                    revert(add(32, error), returndata_size)
                }
            } else {
                revert InitializationFunctionReverted(_init, _calldata);
            }
        }
    }

    function enforceHasContractCode(address _contract, string memory _errorMessage) internal view {
        uint256 contractSize;
        assembly {
            contractSize := extcodesize(_contract)
        }
        require(contractSize > 0, _errorMessage);
    }
}
","keccak256":"0xcaa7ed67af8cf7fe50033f6d06c56cab2bd3392aceceb782660e07153c2c06db","license":"MIT"},"contracts/libraries/LibPrice.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@chainlink/contracts/src/v0.8/Denominations.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV2V3Interface.sol";

import { LibDiamond } from "./LibDiamond.sol";
import { InvalidPrice, FeedNotFound } from "../utils/GenericErrors.sol";
import { AppStorage } from "../AppStorage.sol";

/**
 * @title LibPrice
 * @dev This library provides functions for fetching and manipulating asset prices.
 */
library LibPrice {
    address constant USD_QUOTE = 0x0000000000000000000000000000000000000348;

    /**
     * @notice Get the current price and round IDs of an asset relative to a unit.
     * @param asset The address of the asset.
     * @param unit The address of the unit (e.g., USD).
     * @return price The current price of the asset in terms of the unit.
     * @return investRoundId The round ID of the asset's price feed.
     * @return stableRoundId The round ID of the unit's price feed.
     */
    function getPrice(address asset, address unit) internal view returns (uint256 price, uint80, uint80) {
        AppStorage storage s = LibDiamond.diamondStorage();

        if (s.feeds[asset] == address(0) || s.feeds[unit] == address(0)) {
            revert FeedNotFound();
        }

        (uint80 investRoundId, int256 assetPrice, , , ) = AggregatorV2V3Interface(s.feeds[asset]).latestRoundData();
        (uint80 stableRoundId, int256 unitPrice, , , ) = AggregatorV2V3Interface(s.feeds[unit]).latestRoundData();

        if (assetPrice == 0 || unitPrice == 0) {
            revert InvalidPrice();
        }

        uint256 unitDecimals = IERC20Metadata(unit).decimals();
        price = (uint256(assetPrice) * (10 ** unitDecimals)) / uint256(unitPrice);

        return (price, investRoundId, stableRoundId);
    }

    /**
     * @notice Get the historical price of an asset relative to a unit at specific round IDs.
     * @param investRoundId The round ID of the asset's price feed.
     * @param stableRoundId The round ID of the unit's price feed.
     * @param asset The address of the asset.
     * @param unit The address of the unit (e.g., USD).
     * @return price The price of the asset in terms of the unit at the specified round IDs.
     */
    function getRoundData(
        uint80 investRoundId,
        uint80 stableRoundId,
        address asset,
        address unit
    ) internal view returns (uint256) {
        AppStorage storage s = LibDiamond.diamondStorage();

        if (s.feeds[asset] == address(0) || s.feeds[unit] == address(0)) {
            revert FeedNotFound();
        }

        (, int256 assetPrice, , , ) = AggregatorV2V3Interface(s.feeds[asset]).getRoundData(investRoundId);
        (, int256 unitPrice, , , ) = AggregatorV2V3Interface(s.feeds[unit]).getRoundData(stableRoundId);

        if (assetPrice == 0 || unitPrice == 0) {
            revert InvalidPrice();
        }

        uint256 unitDecimals = IERC20Metadata(unit).decimals();
        uint256 price = (uint256(assetPrice) * (10 ** unitDecimals)) / uint256(unitPrice);

        return price;
    }

    /**
     * @notice Get the price of an asset in USD.
     * @param asset The address of the asset.
     * @return price The price of the asset in USD
     */
    function getUSDPrice(address asset) internal view returns (uint256) {
        AppStorage storage s = LibDiamond.diamondStorage();

        if (s.feeds[asset] == address(0)) {
            revert FeedNotFound();
        }
        (, int256 assetPrice, , , ) = AggregatorV2V3Interface(s.feeds[asset]).latestRoundData();
        if (assetPrice == 0) {
            revert InvalidPrice();
        }
        return uint256(assetPrice);
    }

    /**
     * @notice Get the price of an asset in USD.
     * @param asset The address of the asset.
     * @param roundId the round for which price is required.
     * @return price The price of the asset based on round Id
     */
    function getPriceBasedOnRoundId(address asset, uint80 roundId) internal view returns (uint256) {
        AppStorage storage s = LibDiamond.diamondStorage();

        if (s.feeds[asset] == address(0)) {
            revert FeedNotFound();
        }
        (, int256 assetPrice, , , ) = AggregatorV2V3Interface(s.feeds[asset]).getRoundData(roundId);
        if (assetPrice == 0) {
            revert InvalidPrice();
        }
        return uint256(assetPrice);
    }
}
","keccak256":"0x2c7062db476d53eb08f7fa8b10a3cbf54424e04b770890df025b62f7a91a95e2","license":"MIT"},"contracts/libraries/LibSwap.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { LibAsset } from "./LibAsset.sol";
import { LibUtil } from "./LibUtil.sol";
import { NoSwapFromZeroBalance, InsufficientBalance, SwapFailed } from "../utils/GenericErrors.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title LibSwap
 * @dev This library provides functions for executing asset swaps.
 */
library LibSwap {
    struct SwapData {
        address callTo; // The address of the contract or dex to execute the swap on.
        address fromAsset; // The address of the asset to swap from.
        address toAsset; // The address of the asset to receive.
        uint256 fromAmount; // The amount of the 'fromAsset' to swap.
        bytes callData; // The call data for the swap.
        address user; // The user initiating the swap.
    }

    /**
     * @notice Emitted when an asset swap has been executed successfully.
     * @param dex The address of the contract or dex used for the swap.
     * @param fromAsset The address of the asset swapped from.
     * @param toAsset The address of the asset received in the swap.
     * @param fromAmount The amount of 'fromAsset' that was swapped.
     * @param receivedAmount The amount of 'toAsset' received in the swap.
     * @param account The address of the user account that initiated the swap.
     */

    event AssetSwapped(
        address dex,
        address fromAsset,
        address toAsset,
        uint256 fromAmount,
        uint256 receivedAmount,
        address account
    );

    /**
     * @notice Execute an asset swap from one asset to another using the provided swap data.
     * @param _swap The swap data containing all necessary information for the swap.
     * @return The amount of 'toAsset' received in the swap.
     * @dev This function transfers 'fromAsset' from the user to this contract, executes the swap,
     * and transfers the received 'toAsset' back to the user.
     */
    function swap(SwapData memory _swap) internal returns (uint256) {
        uint256 fromAmount = _swap.fromAmount;
        if (fromAmount == 0) revert NoSwapFromZeroBalance();

        LibAsset.transferFrom(_swap.fromAsset, _swap.user, address(this), fromAmount);

        uint256 initialReceivingAssetBalance = LibAsset.balanceOf(_swap.toAsset, address(this));

        LibAsset.maxApprove(_swap.fromAsset, _swap.callTo, _swap.fromAmount);

        // solhint-disable-next-line avoid-low-level-calls
        (bool success, bytes memory res) = _swap.callTo.call(_swap.callData);
        if (!success) {
            string memory reason = LibUtil.getRevertMsg(res);
            revert(reason);
        }

        uint256 newBalance = LibAsset.balanceOf(_swap.toAsset, address(this));
        uint256 receivedAmount = newBalance - initialReceivingAssetBalance;

        if (receivedAmount == 0) {
            revert SwapFailed();
        }

        LibAsset.transfer(_swap.toAsset, _swap.user, receivedAmount);

        emit AssetSwapped(_swap.callTo, _swap.fromAsset, _swap.toAsset, _swap.fromAmount, receivedAmount, _swap.user);

        return receivedAmount;
    }
}
","keccak256":"0x97d0b9ff55ff5eb11fd67677ccde90384ff4c072b1ef79cffd65c8029a03b598","license":"MIT"},"contracts/libraries/LibTime.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { TimeUnit } from "../AppStorage.sol";

error InvalidUnit();

/**
 * @title LibTime
 * @dev This library provides functions for time-related calculations.
 */
library LibTime {
    /**
     * @notice Convert a given time value to seconds based on the specified time unit.
     * @param time The time value to convert.
     * @param unit The time unit (e.g., TimeUnit.HOURS, TimeUnit.DAYS).
     * @return The time value converted to seconds.
     * @dev Reverts with `InvalidUnit` error if an unsupported time unit is provided.
     */
    function convertToSeconds(uint256 time, TimeUnit unit) internal pure returns (uint256) {
        if (unit == TimeUnit.HOURS) {
            return time * 3600;
        } else if (unit == TimeUnit.DAYS) {
            return time * 86400;
        } else {
            revert InvalidUnit();
        }
    }

    /**
     * @notice Check if a time difference condition is met.
     * @param presentTime The current time.
     * @param executionTime The execution time to consider (0 for immediate execution).
     * @param targetTime The target time for comparison.
     * @return A boolean indicating whether the time difference condition is met.
     */

    function getTimeDifference(
        uint256 presentTime,
        uint256 executionTime,
        uint256 targetTime
    ) internal pure returns (bool) {
        if (executionTime == 0) {
            return true;
        }
        bool timeDifference = targetTime <= presentTime - executionTime ? true : false;
        return timeDifference;
    }
}
","keccak256":"0x9cc3be57e1b3ec593e0008b82036cd72e275fd3a76eec2c67edff07b408b09bd","license":"MIT"},"contracts/libraries/LibTrade.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { IERC20Metadata } from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import { HighImpact } from "../utils/GenericErrors.sol";

/**
 * @title LibTrade
 * @dev This library provides functions for calculating exchange rates and validating slippage.
 */
library LibTrade {
    uint256 public constant MAX_PERCENTAGE = 10000;

    /**
    @dev Calculate exchange rate given input and output amounts
    @param fromAsset Address of the asset that was used to swap
    @param fromAmount Amount of the asset that was used to swap
    @param toAmount Amount of the asset that was received from swap
    @return uint256 Returns the exchange rate in toAsset unit
     */
    function calculateExchangeRate(
        address fromAsset,
        uint256 fromAmount,
        uint256 toAmount
    ) internal view returns (uint256) {
        IERC20Metadata _fromToken = IERC20Metadata(fromAsset);
        uint256 fromDecimals = _fromToken.decimals();
        return ((toAmount * (10 ** fromDecimals)) / fromAmount);
    }

    /**
     * @notice Validate the Impact of a swap.
     * @param exchangeRate The calculated exchange rate for the swap.
     * @param price The reference price for the swap.
     * @param maxImpact The maximum allowed Impact percentage.
     * @param isBuy A flag indicating if it's a buy operation (true) or not (false).
     * @return uint256 Returns the calculated Impact percentage.
     */
    function validateImpact(
        uint256 exchangeRate,
        uint256 price,
        uint256 maxImpact,
        bool isBuy
    ) internal pure returns (uint256) {
        uint256 impact = (price * MAX_PERCENTAGE) / exchangeRate;

        if (isBuy && impact < MAX_PERCENTAGE && MAX_PERCENTAGE - impact > maxImpact) revert HighImpact();
        if (!isBuy && impact > MAX_PERCENTAGE && impact - MAX_PERCENTAGE > maxImpact) revert HighImpact();
        uint256 impactValue;
        if (isBuy && impact < MAX_PERCENTAGE) {
            impactValue = MAX_PERCENTAGE - impact;
        } else if (!isBuy && impact > MAX_PERCENTAGE) {
            impactValue = impact - MAX_PERCENTAGE;
        }
        return impactValue;
    }
}
","keccak256":"0x96c2b97099929cfb81b5e161e3c0718952c6aea1a09573617c0dca820485b6a0","license":"MIT"},"contracts/libraries/LibUtil.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "./LibBytes.sol";

/**
 * @title LibUtil
 * @dev This library provides utility functions for working with revert messages.
 */
library LibUtil {
    using LibBytes for bytes;

    /**
     * @notice Get a revert message from transaction result data.
     * @param _res The transaction result data to extract the revert message from.
     * @return string The revert message or a "Transaction reverted silently" message if none is found.
     */
    function getRevertMsg(bytes memory _res) internal pure returns (string memory) {
        // If the _res length is less than 68, then the transaction failed silently (without a revert message)
        if (_res.length < 68) return "Transaction reverted silently";
        bytes memory revertData = _res.slice(4, _res.length - 4); // Remove the selector which is the first 4 bytes
        return abi.decode(revertData, (string)); // All that remains is the revert string
    }

    /**
     * @notice Used the get the ID of the current chain.
     * @return id The chain ID
     */
    function getChainID() internal view returns (uint256) {
        uint256 id;
        assembly {
            id := chainid()
        }
        return id;
    }
}
","keccak256":"0x2ad03d87c101071a5a415a5dce0efaa23a1d9b1ea0d56fc2746094d89598e061","license":"MIT"},"contracts/utils/GenericErrors.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

error NoSwapFromZeroBalance();
error InsufficientBalance(uint256 required, uint256 balance);
error SwapFailed();
error TransferFailed();
error InvalidExchangeRate(uint256 required, uint256 actual);
error InvalidPrice();
error InvalidImpact();
error HighImpact();
error InvalidInvestToken();
error InvalidStableToken();
error TokensMustDiffer();
error AtLeastOneOptionRequired();
error InvalidInvestAmount();
error FloorValueZero();
error InvalidSellType();
error InvalidSellValue();
error BuyAndSellAtMisorder();
error InvalidStableAmount();
error InvalidBuyType();
error InvalidBuyValue();
error InvalidFloorValue();
error InvalidFloorType();
error InvalidSellTypeDCA();
error FloorValueGreaterThanBuyValue();
error FloorValueGreaterThanSellValue();
error BothBuyTwapAndBTD();
error BuyDCAWithoutBuy();
error BuyTwapTimeInvalid();
error BuyTwapTimeUnitNotSelected();
error BothSellTwapAndSTR();
error SellDCAWithoutSell();
error SellTwapTimeUnitNotSelected();
error SellTwapTimeInvalid();
error SellTwapOrStrWithoutSellDCAUnit();
error SellDCAUnitWithoutSellDCAValue();
error StrWithoutStrType();
error BTDWithoutBTDType();
error BuyDCAWithoutBuyDCAUnit();
error BuyDCAUnitWithoutBuyDCAValue();
error InvalidHighSellValue();
